static void cwal_finalizer_f_func_state( cwal_engine * e, void * m ){
  s2_func_state *fs = (s2_func_state*)m;
  s2_engine * se = s2_engine_from_state(e);
  assert(m);
  assert(se);
  s2_func_state_free( se, fs );
}

