    case 0x0000f61c: W("is-newing",TYPEINFO_ISNEWING);
    case 0x0000932c: W("isnumber",TYPEINFO_ISNUMBER);
    case 0x0000f84b: W("is-number",TYPEINFO_ISNUMBER);
    case 0x00012a04: W("isnumeric",TYPEINFO_ISNUMERIC);
